import React from 'react';
import { StyleSheet, Text, View } from 'react-native';
import { NeuView } from 'react-native-neu-element';
// import Svg, {
//     Circle,
//     Path,
//   } from 'react-native-svg';
  

const Header = ({
    level, chapterTitle, chapterLine
}) => (
    <NeuView color='#ffffff' height={180} width={390} borderRadius={30}>      
        <View style = {styles.container}>
            <Text>{level}</Text>
            <Text>{"\n"}</Text>
            <View style = {styles.inRow}>
                <Text style = {styles.styleTitle}>{chapterTitle} :</Text>  
                <Text style = {styles.boldText}> {chapterLine}</Text>
            </View>
            {/* <Svg height="52%" width="52%" viewBox="0 0 52 52" fill="none">
                <Circle
                    cx="26"
                    cy="26"
                    r="23"
                    fill="#EDF0EF"
                    stroke="#D0CECE"
                    strokeWidth="6"
                />
                <Path d="M20.81 19.87V15.1H29.12V37H23.78V19.87H20.81Z" fill="black"/>
            </Svg> */}

        </View>
    </NeuView>
);

const styles = StyleSheet.create({
    container: {
        alignItems: 'center',
    },
    styleTitle: {
        color: '#3C885E',
        fontWeight: 'bold'
    },
    inRow: {
        flexDirection: 'row'
    },
    boldText: {
        fontWeight: 'bold'
    }
})

export default Header;
